import React from "react";
import pencillImg from "../images/pencill.svg";
import api from "../utils/Api.js";

function Main({ onEditAvatar, onEditProfile, onAddPlace }) {
  const [userName, setUserName] = useState("");
  const [userDescription, setUserDescription] = useState("");
  const [userAvatar, setUserAvatar] = useState("");

  return (
    <main className="main">
      <section className="profile">
        <img
          className="profile__avatar"
          src={require("../images/ursula.png")}
          alt="аватар"
        />
        <button
          className="profile__edit-avatar"
          type="button"
          onClick={onEditAvatar}
        >
          <img
            className="profile__pencil"
            src={pencillImg}
            require
            alt="Карандаш"
          />
        </button>
        <div className="profile__info">
          <div className="profile__info-description">
            <h1 className="profile__name">Урсула Крёбер</h1>
            <p className="profile__job">Американская писательница</p>
          </div>
          <button
            className="profile__button-edit"
            type="button"
            aria-label="Редактировать"
            onClick={onEditProfile}
          ></button>
        </div>
        <button
          className="profile__button-add"
          type="button"
          aria-label="Добавить"
          onClick={onAddPlace}
        ></button>
      </section>
      <section className="mesta"></section>
    </main>
  );
}

export default Main;
