import React, { useState, useEffect } from "react";
import PopupWithForm from "./PopupWithForm";

function AddPlacePopup(props) {
  const [name, setName] = useState("");
  const [link, setLink] = useState("");
  const [error, setError] = useState("");

  function handleNewNameChange(event) {
    setName(event.target.value);
  }

  function handleNewLinkChange(event) {
    setLink(event.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();
    setError(""); // Clear previous error message

    // Send request to the server
    props.onAddPlace(
      {
        name,
        link,
      },
      setError // Pass setError as a callback to handle errors
    );
  }

  function handleClose() {
    setName("");
    setLink("");
    setError(""); // Clear the error message when the popup is closed
    props.onClose();
  }

  useEffect(() => {
    if (!props.isOpen) {
      setName("");
      setLink("");
      setError(""); // Clear the error message when the popup is closed
    }
  }, [props.isOpen]);

  return (
    <PopupWithForm
      title="Новое место"
      name="new-card"
      isOpen={props.isOpen}
      onClose={handleClose}
      onSubmit={handleSubmit}
      textButton={props.isLoading ? "Добавление..." : "Добавить"}
    >
      {/* Input fields and error messages */}
    </PopupWithForm>
  );
}

export default AddPlacePopup;
