  //       navigate("/sign-in");
  //     }
  //   } catch (error) {
  //     console.log(props.isRegistrationSuccessful);
  //   }
  // }

  return (
    <AuthenticationForm
      title="Регистрация"
      buttonText="Зарегистрироваться"
      onSubmit={handleSubmit}
    />
  );
}

export default Register;
