import React, { useState, useEffect } from "react";
import PopupWithForm from "./PopupWithForm";

function AddPlacePopup(props) {
  const [name, setName] = useState("");
  const [link, setLink] = useState("");
  const [isAdding, setIsAdding] = useState(false);

  function handleNewNameChange(event) {
    setName(event.target.value);
  }

  function handleNewLinkChange(event) {
    setLink(event.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();

    setIsAdding(true);

    new Promise((resolve, reject) => {
      props
        .onAddPlace({
          name,
          link,
        })
        .then(() => {
          resolve();
        })
        .catch((error) => {
          reject(error);
        });
    })
      .then(() => {
        console.log("Карточка успешно добавлена");
        props.onClose();
        setIsAdding(false);
      })
      .catch((error) => {
        console.log("Ошибка добавления карточки:", error);
        setIsAdding(false);
      });
  }

  useEffect(() => {
    if (!props.isOpen) {
      setName("");
      setLink("");
    }
  }, [props.isOpen]);

  return (
    <PopupWithForm
      title="Новое место"
      name="new-card"
      isOpen={props.isOpen}
      onClose={props.onClose}
      onSubmit={handleSubmit}
      textButton={isAdding ? "Добавление..." : "Добавить"}
    >
      <input
        minLength="4"
        maxLength="200"
        className="form__field form__text"
        id="form__field-card-text"
        name="name"
        value={name}
        onChange={handleNewNameChange}
        placeholder="Название"
        required
      />
      <span className="form__field-card-text-error form__field-error text-input-error">
        Текст должен быть не короче 2 симв. Длина текста сейчас: 1 символ.
      </span>
      <input
        type="url"
        className="form__field form__text"
        id="form__field-card-image"
        name="link"
        value={link}
        onChange={handleNewLinkChange}
        placeholder="Ссылка на картинку"
        required
      />
      <span className="form__field-card-image-error form__field-error image-input-error">
        Введите URL
      </span>
    </PopupWithForm>
  );
}

export default AddPlacePopup;
