import React, { useState } from "react";
import Header from "../components/Header.js";
import Main from "../components/Main.js";
import Footer from "../components/Footer.js";
import PopupWithForm from "./PopupWithForm.js";

export function App() {
  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = useState(false);
  const [isEditProfilePopupOpen, setEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = useState(false);
  const [userName, setUserName] = useState("");
  const [userDescription, setUserDescription] = useState("");
  const [userAvatar, setUserAvatar] = useState("");

  const handleEditAvatarClick = (event) => {
    setEditAvatarPopupOpen(true);
  };

  const handleEditProfileClick = (event) => {
    setEditProfilePopupOpen(true);
  };

  const handleAddPlaceClick = (event) => {
    setAddPlacePopupOpen(true);
  };

  const closeAllPopups = (event) => {
    console.log("xnj");
    setEditProfilePopupOpen(false);
    setAddPlacePopupOpen(false);
    setEditAvatarPopupOpen(false);
  };

  useEffect(() => {
    // Запрос к API за пользовательскими данными
    api
      .getUserInfo()
      .then((data) => {
        setUserName(data.name);
        setUserDescription(data.about);
        setUserAvatar(data.avatar);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <body className="page">
      <Header />
      <Main
        onEditAvatar={handleEditAvatarClick}
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleAddPlaceClick}
        userName={userName}
        userDescription={userDescription}
        userAvatar={userAvatar}
      />
      <Footer />

      <template id="mesto">
        <div className="mesto">
          <button
            className="mesto__delete"
            type="button"
            aria-label="Удалить"
          ></button>
          <img
            className="mesto__img"
            src={require("../images/photo_2022-12-07_15-50-21.jpg")}
            alt="фотка-поста"
          />
          <div className="mesto__non-image">
            <h2 className="mesto__title">Грибыныч</h2>
            <div className="mesto__like-container">
              <button
                className="mesto__like"
                type="button"
                aria-label="Лайк"
              ></button>
              <p className="mesto__amount-like">0</p>
            </div>
          </div>
        </div>
      </template>

      <PopupWithForm
        title="Редактировать профиль"
        name="edit"
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
      >
        <input
          value=""
          placeholder="Имя"
          minLength="2"
          maxLength="40"
          className="form__field form__text form__field_text_name"
          id="name-input"
          name="name"
          required
        />
        <span className="form__field-error name-input-error">
          Текст должен быть не короче 2 симв. Длина текста сейчас: 1 символ.
        </span>
        <input
          value=""
          placeholder="О себе"
          minLength="4"
          maxLength="200"
          required
          className="form__field form__text form__field_text_job"
          id="job-input"
          name="about"
        />
        <span className="form__field-error form__field-error job-input-error">
          Расскажите о себе
        </span>
      </PopupWithForm>

      <PopupWithForm
        title="Новое место"
        name="new-card"
        isOpen={isAddPlacePopupOpen}
        onClose={closeAllPopups}
      >
        <input
          value=""
          minLength="4"
          maxLength="40"
          className="form__field form__text"
          id="form__field-card-text"
          name="name"
          placeholder="Название"
          required
        />
        <span className="form__field-card-text-error form__field-error text-input-error">
          Текст должен быть не короче 2 симв. Длина текста сейчас: 1 символ.
        </span>
        <input
          value=""
          type="url"
          className="form__field form__text"
          id="form__field-card-image"
          name="link"
          placeholder="Ссылка на картинку"
          required
        />
        <span className="form__field-card-image-error form__field-error image-input-error">
          Введите URL
        </span>
      </PopupWithForm>

      <div className="popup popup_type_confirmation">
        <div className="popup__container">
          <button type="button" className="popup__close"></button>
          <form className="popup__form">
            <h2 className="popup__title">Вы уверены?</h2>
            <button type="submit" className="popup__submit">
              Да
            </button>
          </form>
        </div>
      </div>

      <PopupWithForm
        title="Обновить аватар"
        name="update-avatar"
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopups}
      >
        <input
          type="url"
          name="avatar"
          value=""
          id="form__field-card-avatar"
          className="form__field form__field_text_avatar"
          placeholder="Введите ссылку URL"
          required
        />
        <span className="form__field-error form__field-card-avatar-error">
          Введите URL
        </span>
      </PopupWithForm>
    </body>
  );
}

export default App;
