import React, { useState, useEffect, useContext } from "react";
import Header from "../components/Header.js";
import Main from "../components/Main.js";
import Footer from "../components/Footer.js";
import PopupWithForm from "./PopupWithForm.js";
import api from "../utils/Api.js";
import ImagePopup from "./ImagePopup.js";
import CurrentUserContext from "../contexts/CurrentUserContext.js";

export function App() {
  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = useState(false);
  const [isEditProfilePopupOpen, setEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = useState(false);

  const [cards, setCards] = useState([]);
  const [selectedCard, setSelectedCard] = useState(null);

  const [currentUser, setCurrentUser] = useState(null);

  const handleEditAvatarClick = (event) => {
    setEditAvatarPopupOpen(true);
  };

  const handleEditProfileClick = (event) => {
    setEditProfilePopupOpen(true);
  };

  const handleAddPlaceClick = (event) => {
    setAddPlacePopupOpen(true);
  };

  const closeAllPopups = (event) => {
    setEditProfilePopupOpen(false);
    setAddPlacePopupOpen(false);
    setEditAvatarPopupOpen(false);
    setSelectedCard(null);
  };

  const handleCardClick = (card) => {
    setSelectedCard(card);
  };

  useEffect(() => {
    api
      .getUserInfo()
      .then((data) => {
        setCurrentUser(data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  useEffect(() => {
    api
      .getInitialCardsData()
      .then((data) => {
        setCards(data);
      })
      .catch((error) => {
        console.log("Ошибка при загрузке карточек:", error);
      });
  }, []);

  return (
    <div className="page">
      <Main
        onEditAvatar={handleEditAvatarClick}
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleAddPlaceClick}
        cards={cards}
        onCardClick={handleCardClick}
        CurrentUserContext={currentUser}
      />
      <Footer />
      <PopupWithForm
        title="Редактировать профиль"
        name="edit"
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
      >
        <input
          placeholder="Имя"
          minLength="2"
          maxLength="40"
          className="form__field form__text form__field_text_name"
          id="name-input"
          name="name"
          required
        />
        <span className="form__field-error name-input-error">
          Текст должен быть не короче 2 симв. Длина текста сейчас: 1 символ.
        </span>
        <input
          placeholder="О себе"
          minLength="4"
          maxLength="200"
          required
          className="form__field form__text form__field_text_job"
          id="job-input"
          name="about"
        />
        <span className="form__field-error form__field-error job-input-error">
          Расскажите о себе
        </span>
      </PopupWithForm>
      <PopupWithForm
        title="Новое место"
        name="new-card"
        isOpen={isAddPlacePopupOpen}
        onClose={closeAllPopups}
      >
        <input
          minLength="4"
          maxLength="40"
          className="form__field form__text"
          id="form__field-card-text"
          name="name"
          placeholder="Название"
          required
        />
        <span className="form__field-card-text-error form__field-error text-input-error">
          Текст должен быть не короче 2 симв. Длина текста сейчас: 1 символ.
        </span>
        <input
          type="url"
          className="form__field form__text"
          id="form__field-card-image"
          name="link"
          placeholder="Ссылка на картинку"
          required
        />
        <span className="form__field-card-image-error form__field-error image-input-error">
          Введите URL
        </span>
      </PopupWithForm>
      <div className="popup popup_type_confirmation">
        <div className="popup__container">
          <button type="button" className="popup__close"></button>
          <form className="popup__form">
            <h2 className="popup__title">Вы уверены?</h2>
            <button type="submit" className="popup__submit">
              Да
            </button>
          </form>
        </div>
      </div>
      <PopupWithForm
        title="Обновить аватар"
        name="update-avatar"
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopups}
      >
        <input
          type="url"
          name="avatar"
          id="form__field-card-avatar"
          className="form__field form__field_text_avatar"
          placeholder="Введите ссылку URL"
          required
        />
        <span className="form__field-error form__field-card-avatar-error">
          Введите URL
        </span>
      </PopupWithForm>
      {selectedCard && (
        <ImagePopup card={selectedCard} onClose={closeAllPopups} />
      )}
    </div>
  );
}

export default App;
