import React, { useState, useEffect } from "react";
import PopupWithForm from "./PopupWithForm";

function AddPlacePopup(props) {
  const [name, setName] = useState("");
  const [link, setLink] = useState("");
  const [nameError, setNameError] = useState("");
  const [linkError, setLinkError] = useState("");

  function handleNewNameChange(event) {
    setName(event.target.value);
    setNameError(""); // Очищаем сообщение об ошибке при изменении значения поля
  }

  function handleNewLinkChange(event) {
    setLink(event.target.value);
    setLinkError(""); // Очищаем сообщение об ошибке при изменении значения поля
  }

  function handleSubmit(e) {
    e.preventDefault();

    if (name.length < 2) {
      setNameError("Название должно содержать минимум 2 символа");
      return;
    }

    if (name.length >= 30) {
      setNameError("Название должно содержать максимум 2 символа");
      return;
    }

    if (!link.startsWith("http://") && !link.startsWith("https://")) {
      setLinkError("Введите корректную ссылку на картинку");
      return;
    }

    setNameError("");
    setLinkError("");

    props.onAddPlace({ name, link });
  }

  function handleClose() {
    setName("");
    setLink("");
    setNameError("");
    setLinkError("");
    props.onClose();
  }

  useEffect(() => {
    if (!props.isOpen) {
      setName("");
      setLink("");
      setNameError("");
      setLinkError("");
    }
  }, [props.isOpen]);

  return (
    <PopupWithForm
      title="Новое место"
      name="new-card"
      isOpen={props.isOpen}
      onClose={handleClose}
      onSubmit={handleSubmit}
      textButton={props.isLoading ? "Добавление..." : "Добавить"}
    >
      <input
        minLength="2"
        maxLength="200"
        className="form__field form__text"
        id="form__field-card-text"
        name="name"
        value={name}
        onChange={handleNewNameChange}
        placeholder="Название"
        required
      />
      {nameError && <span className="form__field-error">{nameError}</span>}

      <input
        type="url"
        className="form__field form__text"
        id="form__field-card-image"
        name="link"
        value={link}
        onChange={handleNewLinkChange}
        placeholder="Ссылка на картинку"
        required
      />
      {linkError && <span className="form__field-error">{linkError}</span>}

      <button
        type="submit"
        className="form__submit-button"
        disabled={nameError || linkError}
      >
        {props.isLoading ? "Добавление..." : "Добавить"}
      </button>
    </PopupWithForm>
  );
}

export default AddPlacePopup;
