  return (
    <header className="header header_opacity">
      <img
        className="header__logo"
        src={logImg}
        require="true"
        alt="логотип-место"
      />
      <div className="header__info">
        {useLocation().pathname === "/sign-in" && (
          <Link to="/sign-up" className="header-text">
            Регистрация
          </Link>
        )}
        {useLocation().pathname === "/sign-up" && (
          <Link to="/sign-in" className="header-text">
            Войти
          </Link>
        )}
        {useLocation().pathname === "/" && (
          <div className="header__info">
            <p className="header-text">{props.email}</p>
            <Link
              to="/sign-in"
              className="header-text"
              onClick={props.onSignOut}
            >
              Выйти
            </Link>
          </div>
        )}
