import React, { useEffect, useState } from "react";
import pencillImg from "../images/pencill.svg";
import Card from "./Card";

function Main({
  onEditAvatar,
  onEditProfile,
  onAddPlace,
  cards,
  onCardClick,
  currentUserContext,
}) {
  return (
    <main className="main">
      <section className="profile">
        <div
          className="profile__avatar"
          style={{ backgroundImage: `url(${currentUserContext})` }}
          alt="аватар"
        />
        <button
          className="profile__edit-avatar"
          type="button"
          onClick={onEditAvatar}
        >
          <img
            className="profile__pencil"
            src={pencillImg}
            require="true"
            alt="Карандаш"
          />
        </button>
        <div className="profile__info">
          <div className="profile__info-description">
            <h1 className="profile__name">{currentUserContext.name}</h1>
            <p className="profile__job">{currentUserContext.about}</p>
          </div>
          <button
            className="profile__button-edit"
            type="button"
            aria-label="Редактировать"
            onClick={onEditProfile}
          ></button>
        </div>
        <button
          className="profile__button-add"
          type="button"
          aria-label="Добавить"
          onClick={onAddPlace}
        ></button>
      </section>
      <section className="mesta">
        {cards.map((card) => (
          <Card card={card} onCardClick={onCardClick} key={card._id} />
        ))}
      </section>
    </main>
  );
}

export default Main;
