import React, { useState, useEffect } from "react";
import PopupWithForm from "./PopupWithForm";

function AddPlacePopup(props) {
  const [name, setName] = useState("");
  const [link, setLink] = useState("");
  const [error, setError] = useState("");

  function handleNewNameChange(event) {
    setName(event.target.value);
  }

  function handleNewLinkChange(event) {
    setLink(event.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();

    setError(""); // Clear any previous error

    props
      .onAddPlace({
        name,
        link,
      })
      .then(() => {
        // Handle successful submission
        setName("");
        setLink("");
      })
      .catch((error) => {
        // Handle error
        setError("Error adding card. Please try again."); // Set the error message
      });
  }

  useEffect(() => {
    if (!props.isOpen) {
      setName("");
      setLink("");
      setError(""); // Clear the error when the popup is closed
    }
  }, [props.isOpen]);

  return (
    <PopupWithForm
      title="Новое место"
      name="new-card"
      isOpen={props.isOpen}
      onClose={props.onClose}
      onSubmit={handleSubmit}
      textButton={props.isLoading ? "Добавление..." : "Добавить"}
    >
      {error && <span className="form__error">{error}</span>}

      <input
        minLength="4"
        maxLength="200"
        className="form__field form__text"
        id="form__field-card-text"
        name="name"
        value={name}
        onChange={handleNewNameChange}
        placeholder="Название"
        required
      />
      <span className="form__field-card-text-error form__field-error text-input-error">
        Текст должен быть не короче 2 симв. Длина текста сейчас: 1 символ.
      </span>
      <input
        type="url"
        className="form__field form__text"
        id="form__field-card-image"
        name="link"
        value={link}
        onChange={handleNewLinkChange}
        placeholder="Ссылка на картинку"
        required
      />
      <span className="form__field-card-image-error form__field-error image-input-error">
        Введите URL
      </span>
    </PopupWithForm>
  );
}

export default AddPlacePopup;
