import React from "react";
import pencillImg from "../images/pencill.svg";

function Main() {
  const handleEditAvatarClick = () => {
    document
      .querySelector(".popup_type_update-avatar")
      .classList.add("popup_open");
  };

  const handleEditProfileClick = () => {
    document.querySelector(".popup_type_edit").classList.add("popup_open");
  };

  const handleAddPlaceClick = () => {
    document.querySelector(".popup_type_new-card").classList.add("popup_open");
  };
  return (
    <main className="main">
      <section className="profile">
        <img
          className="profile__avatar"
          src={require("../images/ursula.png")}
          alt="аватар"
        />
        <button
          className="profile__edit-avatar"
          type="button"
          onClick={handleEditAvatarClick}
        >
          <img
            className="profile__pencil"
            src={pencillImg}
            require
            alt="Карандаш"
          />
        </button>
        <div className="profile__info">
          <div className="profile__info-description">
            <h1 className="profile__name">Урсула Крёбер</h1>
            <p className="profile__job">Американская писательница</p>
          </div>
          <button
            className="profile__button-edit"
            type="button"
            aria-label="Редактировать"
            onClick={handleEditProfileClick}
          ></button>
        </div>
        <button
          className="profile__button-add"
          type="button"
          aria-label="Добавить"
          onClick={handleAddPlaceClick}
        ></button>
      </section>
      <section className="mesta"></section>
    </main>
  );
}

export default Main;
